import './App.css';
import React, { useState } from 'react';
import { Provider, useSelector, useDispatch } from 'react-redux';
import { BrowserRouter, Route, Routes, Link } from 'react-router-dom';
import { createStore } from 'redux';

import Header from './components/Header';
import Index from './components/Index';
import Process from './components/Process';
import Product from './components/Product';
import Test from './components/Test';

type Doc = {
  id: number;
  name: string;
};

interface Num {
  type: string;
}

const initialState = { num: 0 };

const reducer = (state: any, action: Num) => {
  switch (action.type) {
    case 'decrement':
      return { ...state, num: state.num - 1 };
    case 'increment':
      return { ...state, num: state.num + 1 };
    default:
      return state;
  }
};

const store = createStore(reducer, initialState);

const App: React.FC = () => {
  const [docu, setDocu] = useState<string>('');
  const [id, setId] = useState<number>(0);
  const [list, setList] = useState<Doc[]>([]);
  const [note, setNote] = useState<string | null>('');

  const nullDocu = () => {
    setDocu(' ');
    console.log(docu);
  };
  const defaultDocu = () => {
    setDocu(docu.slice(1));
  };
  const handleDocList = () => {
    if (docu) {
      setList([
        ...list,
        {
          id: id,
          name: docu,
        },
      ]);
      sessionStorage.setItem(String(id), docu);
      setNote(sessionStorage.getItem('0'));
      setDocu('');
      setId(id + 1);
    }
  };

  console.log(note);
  console.log(sessionStorage);

  // ドキュメントの名前を入力して登録する
  const docuName = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (docu.length < 33) {
      setDocu(event.target.value);
      console.log(docu);
    }
  };
  // console.log(list);

  return (
    <BrowserRouter>
      <Header />
      <div className='App'>
        <div className='Top'>
          <div className='TopContainer'>
            <div className='TopContainer__Left'>
              <div className='TopHead'>
                <div className='TopButton'>
                  <input
                    className='TopButton__name'
                    type='text'
                    id='name'
                    onChange={docuName}
                    value={!docu ? '新しいドキュメントを作成する' : docu}
                    onBlur={defaultDocu}
                    onClick={nullDocu}
                  ></input>
                  <button className='TopButton__button' type='submit' onClick={handleDocList}>
                    作成する
                  </button>
                </div>
                <div className='TopHead__title'>＜ドキュメント一覧＞</div>
                <Provider store={store}>
                  <ChildComponentUseReactRedux />
                </Provider>
              </div>
              <ul className='TopList'>
                {list.map((docu) => (
                  <li key={docu.id} className='TopList__item'>
                    <div className='TopList__itemName'>
                      <Link to={`./${docu.id}/${docu.name}`} className='TopList__itemName'>
                        <p>{docu.name}</p>
                      </Link>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
            <div className='TopContainer__Left'>
              <div className='TopHead'>
                <div className='TopButton'>
                  <input
                    className='TopButton__name'
                    type='text'
                    id='name'
                    onChange={docuName}
                    value={!docu ? '新しいドキュメントを作成する' : docu}
                    onBlur={defaultDocu}
                    onClick={nullDocu}
                  ></input>
                  <button className='TopButton__button' type='submit' onClick={handleDocList}>
                    作成する
                  </button>
                </div>
                <div className='TopHead__title'>＜ドキュメント一覧＞</div>
                <Provider store={store}>
                  <ChildComponentUseReactRedux />
                </Provider>
              </div>
              <ul className='TopList'>
                {list.map((docu) => (
                  <li key={docu.id} className='TopList__item'>
                    <div className='TopList__itemName'>
                      <Link to={`./${docu.id}/${docu.name}`} className='TopList__itemName'>
                        <p>{docu.name}</p>
                      </Link>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
            <div className='TopContainer__Right'>
              <Routes>
                <Route path='/' element={<Index />} />
                <Route path='/top' element={<Process />} />
                <Route path='/test' element={<Test />} />
                <Route path='/:id/:name' element={<Product />} />
              </Routes>
            </div>
          </div>
        </div>
      </div>
      {/* <Footer /> */}
    </BrowserRouter>
  );
};

const ChildComponentUseReactRedux = () => {
  const selector = (state: any) => {
    return state.num;
  };

  const num = useSelector(selector);
  const dispatch = useDispatch();
  return (
    <div>
      <h3>Using useSelector, useDispatch</h3>
      Number: {num}
      <button onClick={() => dispatch({ type: 'increment' })}>+</button>
      <button onClick={() => dispatch({ type: 'decrement' })}>-</button>
    </div>
  );
};

export default App;
